mapper.select:
SELECT p.pm_key AS pmKey, p.folder_token AS folder_token, p.project_short_name AS projectShortName, p.redmine_url AS redmineUrl, p.start_day AS startDay, p.pm_ou AS pmOu, p.p_key AS pKey, p.id AS id, fw.url AS weeklyReportUrl, fw.week_num weekNum, fw.file_name AS fileName, fw.file_token AS fileToken, fs.`name` AS pmName FROM r_project_info p LEFT JOIN fs_weekly_project_report fw ON p.id = fw.r_pid LEFT JOIN fs_user_member_info fs ON p.pm_ou = fs.member_id WHERE fw.file_token IS NOT NULL AND fw.is_delete = 0 AND p.is_delete = 0 AND p.weekly_type = 0
AND fw.week_num = #{weekNumber}
AND fw.week_num = WEEKOFYEAR(NOW())
AND p.id = #{id}

SELECT fw.file_name AS fileName, fw.url AS weeklyReportUrl, fw.week_num AS weekNum FROM r_project_info p, fs_weekly_project_report fw WHERE p.id = fw.r_pid AND fw.file_token IS NOT NULL AND p.id = #{id}
AND fw.file_name like concat('%',#{name},'%')
AND fw.is_delete = 0 ORDER BY week_num DESC
