mapper.select:
SELECT SUM(CASE WHEN sc.`work` = 1 THEN (CASE WHEN (te.hours - 8) > 0 THEN (te.hours - 8) ELSE 0 END) ELSE 0 END) AS addWork ,spentOn AS `day` ,projectId ,userName ,userId FROM rd_time_entry te, r_project_info rp, s_holiday_config sc
AND te.projectId = rp.p_id AND rp.is_delete = 0 AND rp.redmine_type = 2 AND sc.`date` = te.spentOn
AND spentOn >= #{vo.startTime}
AND spentOn <= #{vo.endTime}
GROUP BY `day`, userName, userId, projectId

SELECT t.userId, t.userName, SUM(t.addWork) AS overtime, SUM(t.hours) AS totalTime, ROUND( SUM(t.hours) / (SUM(t.hours) - SUM( CASE WHEN t.addWork < 0 THEN 0 ELSE t.addWork END )), 2) employeeLoad FROM ( SELECT res.spentOn, res.userId, res.userName, SUM(res.hours) AS hours, SUM(CASE WHEN sc.`work` = 1 THEN (CASE WHEN (res.hours - 8) > 0 THEN (res.hours - 8) ELSE 0 END) WHEN DAYOFWEEK(res.spentOn) IN (1,7) AND sc.`work` = 0 THEN res.hours ELSE 0 END) AS addWork FROM ( SELECT SUM( CASE WHEN (SELECT COUNT(*) FROM r_project_info WHERE p_id = re.projectId AND redmine_type = 2) > 0 THEN re.hours ELSE 0 END ) hours, re.userId, re.userName, re.spentOn FROM `rd_time_entry` re, `r_project_info` rp, `fs_user_member_info` fu
AND rp.redmine_type = 2 AND re.projectId = rp.p_id AND fu.`name` = REPLACE(re.userName,' ','') AND fu.resign = 0
AND re.projectId = #{vo.pid}
AND re.spentOn >= #{vo.startTime}
AND re.spentOn <= #{vo.endTime}
AND re.userId = #{vo.userId}
GROUP BY re.spentOn, re.userId, re.userName ) res, s_holiday_config sc WHERE sc.date = res.spentOn GROUP BY res.spentOn,res.userId,res.userName ) t GROUP BY t.userId,t.userName

SELECT re.hours, re.userName, rp.p_name AS pname, re.spentOn FROM `rd_time_entry` re, r_project_info rp
re.projectId = rp.p_id AND rp.redmine_type = 2 AND rp.is_delete = 0
AND re.projectId = #{vo.pid}
AND re.userId = #{vo.userId}
AND re.spentOn >= #{vo.startTime}
AND re.spentOn <= #{vo.endTime}
ORDER BY re.spentOn DESC

SELECT re.hours, re.projectId FROM `rd_time_entry` re, r_redmine_user_info ri
ri.assignee_id = re.userId AND ri.redmine_type = 2 AND ri.is_delete = 0
AND re.spentOn >= #{vo.startTime}
AND re.spentOn <= #{vo.endTime}

SELECT SUM(CASE WHEN sc.`work` = 1 THEN ( CASE WHEN ( te.hours - 8 ) > 0 THEN ( te.hours - 8 ) ELSE 0 END ) ELSE 0 END ) AS overtime, SUM(te.hours) AS peopleHours, projectId AS pid, IFNULL(rp.budget,0) budget, DATE_FORMAT(te.spentOn,'%Y%m') AS months, rp.project_short_name AS shortName FROM rd_time_entry te, r_project_info rp, s_holiday_config sc WHERE te.projectId = rp.p_id AND rp.is_delete = 0 AND rp.redmine_type = 2 AND sc.`date` = te.spentOn
AND te.spentOn >= #{vo.startTime}
AND te.spentOn <= #{vo.endTime}
GROUP BY te.projectId,rp.budget,months,shortName
